import React, { useState } from 'react';
import { BiSearch } from 'react-icons/bi';
import { BsPerson } from 'react-icons/bs';
import { HiOutlineMenuAlt4 } from 'react-icons/hi';
import { AiOutlineClose } from 'react-icons/ai';
import { FaFacebook, FaInstagram, FaPinterest, FaTwitter, FaYoutube } from 'react-icons/fa';
import { Link } from 'react-router-dom';

function Navbar() {
    const [nav, setNav] = useState(false);

    const handleNav = () => setNav(!nav);

    const navigation = [
        {
            id: 1,
            link: 'home',
            name: 'Home',
        },
        {
            id: 2,
            link: 'services',
            name: 'Services',
        },
        {
            id: 3,
            link: 'alert',
            name: 'Alert',
        },
        {
            id: 4,
            link: 'manage',
            name: 'Manage',
        },
    
    ];

    return (
        <div className={`bg-gray-900 text-white p-4 sm:px-8 sm:py-4 ${nav ? 'shadow-md' : ''}`}>
            <div className="container mx-auto flex justify-between items-center">
                <div className="flex items-center">
                    <div className="logo text-3xl font-extrabold tracking-wider text-blue-500">
                        Natural Disaster
                    </div>
                </div>
                <ul className="hidden sm:flex gap-8 text-lg no-underline">
                    {navigation.map((item) => (
                        <li key={item.id}>
                            <Link
                                to={`/${item.link}`}
                                className={`transition-colors duration-300 hover:text-blue-200 ${
                                    nav ? 'hover:text-blue-200' : ''
                                }`}
                            >
                                {item.name}
                            </Link>
                        </li>
                    ))}
                </ul>
                <div className="flex items-center">
                    <BiSearch className="text-3xl cursor-pointer mr-4 hover:text-blue-200" />
                    <Link to="/login">
                    <BsPerson  className="text-3xl cursor-pointer hover:text-blue-200" />
                    </Link>
                </div>
                <div className="hamburger text-3xl cursor-pointer sm:hidden" onClick={handleNav}>
                    {nav ? <AiOutlineClose /> : <HiOutlineMenuAlt4 />}
                </div>
            </div>
            <div className={`${nav ? 'block' : 'hidden'} sm:hidden bg-gray-800 text-white py-4 px-8 transition-all duration-300`}>
                <ul className="mb-4 text-center">
                    {navigation.map((item) => (
                        <li key={item.id} className="mb-2">
                            <Link
                                to={`/${item.link}`}
                                className="text-lg hover:text-gray-300 transition-colors duration-300"
                                onClick={handleNav}
                            >
                                {item.name}
                            </Link>
                        </li>
                    ))}
                </ul>
                <div className="flex justify-around items-center my-4">
                    <FaFacebook className="text-3xl cursor-pointer hover:scale-110 transform duration-300" />
                    <FaInstagram className="text-3xl cursor-pointer hover:scale-110 transform duration-300" />
                    <FaTwitter className="text-3xl cursor-pointer hover:scale-110 transform duration-300" />
                    <FaPinterest className="text-3xl cursor-pointer hover:scale-110 transform duration-300" />
                    <FaYoutube className="text-3xl cursor-pointer hover:scale-110 transform duration-300" />
                </div>
                <div className="flex flex-col items-center">
                    <button className="w-full bg-blue-500 text-white py-2 px-4 rounded-md mb-2">
                        Search
                    </button>
                    <Link to="/login" className="w-full bg-blue-500 text-white py-2 px-4 rounded-md mb-2">
                        Account
                    </Link>
                </div>
            </div>
        </div>
    );
}

export default Navbar;
