import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EditDisasterModal({ isOpen, onClose, selectedDisaster, onUpdate }) {
  const [editedDisaster, setEditedDisaster] = useState(selectedDisaster);

  useEffect(() => {
    setEditedDisaster(selectedDisaster);
  }, [selectedDisaster]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedDisaster((prevDisaster) => ({
      ...prevDisaster,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:8080/disaster/updateDisaster/${selectedDisaster.id}`, editedDisaster);
      onUpdate(response.data); // Assuming the server returns the updated disaster object
      onClose();
    } catch (error) {
      console.error('Error updating disaster:', error);
    }
  };

  return (
    <>
      {isOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-8 rounded-md">
            <h2 className="text-2xl font-bold mb-4">Edit Disaster</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="disasterType">
                  Disaster Type
                </label>
                <input
                  type="text"
                  id="disasterType"
                  name="disasterType"
                  value={editedDisaster.disasterType || ''}
                  onChange={handleChange}
                  className="border border-gray-300 rounded-md p-2 w-full"
                />
              </div>
              {/* Include other input fields similarly */}
              <div className="flex justify-end">
                <button
                  type="button"
                  onClick={onClose}
                  className="bg-gray-500 text-white p-2 rounded hover:bg-gray-700 mr-2"
                >
                  Close
                </button>
                <button type="submit" className="bg-blue-500 text-white p-2 rounded hover:bg-blue-800">
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </>
  );
}

export default EditDisasterModal;
